sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (!current.ip_address) {
        var msgErr = "Falha ao executar Business Rule 'CMDB BR retire rules for ICs'." +
            "\n- Error: IC sem IP.\n- IC: " + current.getValue('name');

        gs.log(msgErr, "BR retroalimentacao CMDB");
        return;
    }

    var srcDiscovery = new Discovery();
    var newTask;
    var shortMsg;
    var descMsg;
    var priorityNum;

    var arrIP = [];
    arrIP.push(current.ip_address);
    var objSchedule = srcDiscovery.getScheduleContainingAnyIP(arrIP);

    if (objSchedule && objSchedule.schedule.valid) {

        var grErrDiscovery = new GlideRecord('automation_error_msg');
        grErrDiscovery.addEncodedQuery('status=0^application=d5069fe9e70332001a310a6103f6a94b');
        grErrDiscovery.query();

        if (grErrDiscovery.find("host", current.ip_address)) {

            newTask = new ITOMTaskUtils();

            shortMsg = "Tarefa aberta automaticamente após identificar que o IC " + current.ip_address +
                " foi retirado com tarefas de Discovery pendentes.";

            descMsg = "Erro de Discover em aberto: " +
                grErrDiscovery.getDisplayValue("error_code");

            priorityNum = 2;

            newTask.createNewTask(current, shortMsg, descMsg, priorityNum);
        }
    } else {

        var objRange = null;
        var rangeUtils = new IPRangeUtils();
        objRange = rangeUtils.findIPExact(current.ip_address);

        if (objRange) {
            newTask = new ITOMTaskUtils();

            shortMsg = "Tarefa aberta automaticamente após identificar que o IC " + current.ip_address +
                " foi retirado com tarefas de Discovery pendentes.";

            descMsg = "Erro: range desativado e/ou sem schedule.\n\n-- Resumo --" +
                "\nRange: " + objRange.range +
                "\nMaior IP: " + objRange.highest_ip +
                "\nAtivo: " + objRange.status +
                "\nDiscovery range: " + objRange.disc_range;

            priorityNum = 1;

            newTask.createNewTask(current, shortMsg, descMsg, priorityNum);
            return;
        }

        var arr_aux = rangeUtils.createArrIP(current.ip_address);

        for (var i = 0; i < arr_aux.length; i++) {
            objRange = rangeUtils.findIPRange(current.ip_address, arr_aux[i]);

            if (objRange) {

                newTask = new ITOMTaskUtils();

                shortMsg = "Tarefa aberta automaticamente após identificar que o IC " + current.ip_address +
                    " foi retirado com tarefas de Discovery pendentes.";

                descMsg = "Erro: range desativado e/ou sem schedule.\n\n-- Resumo --" +
                    "\nRange: " + objRange.range +
                    "\nMaior IP: " + objRange.highest_ip +
                    "\nAtivo: " + objRange.status +
                    "\nDiscovery range: " + objRange.disc_range;

                priorityNum = 1;

                newTask.createNewTask(current, shortMsg, descMsg, priorityNum);
                return;
            }
        }
        newTask = new ITOMTaskUtils();

        shortMsg = "Tarefa aberta automaticamente após identificar que o IC " + current.ip_address +
            " foi retirado com tarefas de Discovery pendentes.";

        descMsg = "Não encontrado range de IP do IC " + current.ip_address +
            " retirado na tabela Discovery IP Range.";

        priorityNum = 1;

        newTask.createNewTask(current, shortMsg, descMsg, priorityNum);
        return;
    }
})(current, previous);
