sys_script_include.script:
var ITOMTaskUtils = Class.create();
ITOMTaskUtils.prototype = {

    createNewTask: function (current, shortMsg, descMsg, priorityNum) {

        var targetTable = "sa_error_handler_task";
        var targetGroup = "f53d408c1b43c418261c63923b4bcbc5"; //

        var newTask = new GlideRecord(targetTable);
        newTask.addEncodedQuery('priority=' + priorityNum + '^stateIN-5,1,2^cmdb_ci=' + current.sys_id);
        newTask.query();

        if (!newTask.next()) {

            newTask.initialize();
            newTask.setValue("short_description", shortMsg);
            newTask.setValue("description", descMsg);
            newTask.setValue("assignment_group", targetGroup);
            newTask.setValue("priority", priorityNum);
            newTask.setValue("cmdb_ci", current.sys_id);
            var wasInserted = newTask.insert();
            if (!wasInserted) {
                var errMsg = "Falha ao criar registro em " + targetTable + ". Erro: " + newTask.getErrorMsg();
                gs.log(errMsg, "BR retroalimentação CMDB");
            }
        }
    },

    type: 'ITOMTaskUtils'
};
